INSERT INTO `tbl_khoa_hoc` (`khoa_hoc_id`, `ten_khoa_hoc`, `hinh_thuc`, `buoi_hoc`, `thoi_gian`, `gia`, `anh_khoa_hoc`, `mo_ta`) VALUES
(1, 'Lập trình C/C++', 'Trực tuyến', 12, '19h30-21h30 thứ 3 và thứ 6 hàng tuần', 850000, 'c.jpg', '<p style=\"box-sizing: border-box; margin: 0px 0px 25px; padding: 0px; color: #666666; font-family: Roboto; font-size: 15px; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Nội dung kh&oacute;a học sẽ dẫn dắt c&aacute;c bạn từ một người chưa biết g&igrave; hoặc đ&atilde; từng biết một ch&uacute;t về lập tr&igrave;nh đến trở th&agrave;nh một người nắm vững v&agrave; th&agrave;nh thục sử dụng c&aacute;c th&agrave;nh phần cơ bản của ng&ocirc;n ngữ lập tr&igrave;nh C++. Đ&acirc;y l&agrave; bước đệm hết sức quan trọng cho bất k&igrave; một học sinh, sinh vi&ecirc;n hay người sắp đi l&agrave;m ng&agrave;nh IT n&agrave;o. Nếu bạn chọn học kh&oacute;a học để l&agrave;m nền tảng, v&aacute; lỗ hổng lập tr&igrave;nh; để học tốt hơn m&ocirc;n học C++ ở trường; để chuẩn bị cho m&ocirc;n Cấu tr&uacute;c dữ liệu v&agrave; giải thuật; để theo đuổi nghề lập tr&igrave;nh nh&uacute;ng, game, hệ thống, IoT&hellip; th&igrave; đ&acirc;y l&agrave; một lựa chọn hợp l&yacute;. C&aacute;c ưu điểm trong kh&oacute;a học của ch&uacute;ng t&ocirc;i gồm:</span></p>\r\n<div class=\"course-list-tab\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #666666; font-family: Roboto; font-size: 15px; background-color: #ffffff;\">\r\n<ul class=\"course-list-tab-icon list-unstyled first\" style=\"box-sizing: border-box; margin: 0px 0px 10px 25px; padding: 0px; list-style: none;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Ph&ugrave; hợp với mọi cấp độ hiện tại của bạn</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Bạn c&oacute; thể học qua video hoặc đọc t&agrave;i liệu b&agrave;i giảng</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Kh&ocirc;ng giới hạn thời gian học trong ng&agrave;y</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Hỗ trợ trực tuyến kh&ocirc;ng phải ra đường mưa nắng</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Kh&ocirc;ng cần phải ra trung t&acirc;m đắt đỏ</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Nội dung đầy đủ v&agrave; cập nhật</span></li>\r\n</ul>\r\n<ul class=\"course-list-tab-icon list-unstyled last\" style=\"box-sizing: border-box; margin: 0px 0px 10px 25px; padding: 0px; list-style: none;\">\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Gi&aacute; cả hợp l&yacute; so với những g&igrave; bạn nhận được</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">C&acirc;u hỏi kiểm tra trắc nghiệm đầy đủ bao qu&aacute;t</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">B&agrave;i tập thực h&agrave;nh ri&ecirc;ng đa dạng cho từng b&agrave;i học</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Trang bị nền tảng ng&ocirc;n ngữ lập tr&igrave;nh C++</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Tự tin trong c&aacute;c b&agrave;i kiểm tra ở trường</span></li>\r\n<li style=\"box-sizing: border-box; margin: 0px; padding: 0px;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; color: #333333;\">Tăng tốc khi học c&aacute;c ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c</span></li>\r\n</ul>\r\n</div>'),
(2, 'Lập trình C#', 'phòng lab', 10, '8h30-10h30 thứ 2, thứ 5 và thứ 7 hàng tuần', 700000, 'csharp.jpg', '<h2 id=\"ftoc-so-luoc-ve-ngon-ngu-lap-trinh-c\" class=\"ftwp-heading\" style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 1.953em; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #4285f4; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Sơ lược về ng&ocirc;n ngữ lập tr&igrave;nh C#&nbsp;</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">L&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh thường được gọi l&agrave; C Sharp. Được thế giới sử dụng rộng r&atilde;i v&igrave; n&oacute; l&agrave; ng&ocirc;n ngữ hiện đại v&agrave; tổng qu&aacute;t. C&oacute; thể bi&ecirc;n dịch tr&ecirc;n nhiều nền tảng m&aacute;y t&iacute;nh kh&aacute;c nhau.</p>\r\n<div class=\"code-block code-block-15 ai-viewport-1 ai-viewport-2\" style=\"box-sizing: border-box; margin: 8px 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; background-color: #ffffff; clear: both; display: inherit !important;\">&nbsp;</div>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">L&agrave; ng&ocirc;n ngữ lập tr&igrave;nh hướng đối tượng v&agrave; l&agrave; một phần của .NET Framework. C&oacute; thể tạo ra c&aacute;c chương tr&igrave;nh c&oacute; giao diện đẹp v&agrave; hiệu quả. N&oacute; l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh được cấu tr&uacute;c v&agrave; dễ học. Tuy nhi&ecirc;n, dễ ở đ&acirc;y l&agrave; khi bạn chăm chỉ v&agrave; ki&ecirc;n tr&igrave; học bạn mới thấy dễ. N&ecirc;n đừng nản ch&iacute; khi mới bắt đầu nha.</p>\r\n<div class=\"code-block code-block-1 ai-viewport-1 ai-viewport-2\" style=\"box-sizing: border-box; margin: 8px 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; background-color: #ffffff; clear: both; display: inherit !important;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\" align=\"center\">\r\n<div id=\"passback_pc_article_mid1_728x90//300x250//336x280\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<div id=\"google_ads_iframe_/21622890900,22527870362/VN_anonyviet.com_pc_article_mid1_728x90//300x250//336x280_0__container__\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0pt none; font: inherit; vertical-align: baseline; width: 300px; height: 0px;\"></div>\r\n</div>\r\n</div>\r\n</div>\r\n<h2 id=\"ftoc-gioi-thieu-ve-khoa-hoc-c\" class=\"ftwp-heading\" style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 1.953em; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #4285f4; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Giới thiệu về kh&oacute;a học C#</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Kh&oacute;a học được chia th&agrave;nh 5 phần với c&aacute;c mức độ kh&aacute;c nhau. Gi&uacute;p bạn tiếp cận được C# từ dễ đến kh&oacute;. Bạn c&oacute; thể tự tạo ra được một ứng dụng C# ho&agrave;n chỉnh nếu học tốt. Vậy kh&oacute;a học n&agrave;y c&oacute; g&igrave; th&uacute; vị v&agrave; c&oacute; đ&aacute;ng để tham gia ?</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\"><img class=\"aligncenter size-full wp-image-7991\" style=\"box-sizing: border-box; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; vertical-align: middle; max-width: 100%; height: auto; clear: both; text-align: center; display: block;\" title=\"Share kh&oacute;a học lập tr&igrave;nh C# với Winform trị gi&aacute; 600K 7\" src=\"https://anonyviet.com/wp-content/uploads/2018/01/visual_basic-01.jpg\" sizes=\"(max-width: 400px) 100vw, 400px\" srcset=\"https://anonyviet.com/wp-content/uploads/2018/01/visual_basic-01.jpg 400w, https://anonyviet.com/wp-content/uploads/2018/01/visual_basic-01-300x210.jpg 300w\" alt=\"Share kh&oacute;a học lập tr&igrave;nh C# trị gi&aacute; 600K\" width=\"400\" height=\"280\" loading=\"lazy\" data-pin-no-hover=\"true\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Giảng vi&ecirc;n sẽ li&ecirc;n tục tạo điều kiện cho bạn thực h&agrave;nh xen kẻ l&yacute; thuyết. Bạn sẽ kh&ocirc;ng &ocirc;m mớ l&yacute; thuyết kh&ocirc; khan v&agrave; ghi ra giấy nữa. Nếu bạn đang muốn cải thiện kiến thức về c&ocirc;ng nghệ .Net để phục vụ cho c&ocirc;ng việc th&igrave; n&ecirc;n tham gia kh&oacute;a học n&agrave;y.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Hướng dẫn cho bạn sử dụng c&ocirc;ng cụ Framework mới nhất. Bạn sẽ được dạy c&aacute;ch tự t&igrave;m t&ograve;i v&agrave; cập nhật c&ocirc;ng cụ về sau. Ngo&agrave;i ra, bạn cũng được tiếp x&uacute;c nhiều với c&aacute;c kiến thức lập tr&igrave;nh hướng đối tượng trong C#.</p>\r\n<div class=\"code-block code-block-2 ai-viewport-1 ai-viewport-2\" style=\"box-sizing: border-box; margin: 8px 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; background-color: #ffffff; clear: both; display: inherit !important;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\" align=\"center\">\r\n<div id=\"passback_pc_article_mid2_728x90//300x250//336x280\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<div id=\"google_ads_iframe_/21622890900,22527870362/VN_anonyviet.com_pc_article_mid2_728x90//300x250//336x280_0__container__\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0pt none; font: inherit; vertical-align: baseline; width: 336px; height: 0px;\"></div>\r\n</div>\r\n</div>\r\n<ins class=\"adsbygoogle\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; background: #fcf8e3; display: block;\" data-ad-client=\"ca-pub-2642640466336649\" data-ad-slot=\"2290057006\" data-ad-format=\"link\"></ins></div>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">R&egrave;n dũa cho bạn kĩ hơn về mặt xử lĩ c&aacute;c vẫn đề chuỗi (String), mảng (Array) v&agrave; Collections. Gi&uacute;p bạn sử dụng tốt v&agrave; thuần thục c&aacute;c control khi thiết kế giao diện Winform. C&oacute; thể l&agrave;m ra giao diện đẹp hơn, hiện đại hơn.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\"><img class=\"aligncenter wp-image-7990\" style=\"box-sizing: border-box; margin: 5px auto 1.25em; padding: 0px; border: 0px; font: inherit; vertical-align: middle; max-width: 100%; height: auto; clear: both; text-align: center; display: block;\" title=\"Share kh&oacute;a học lập tr&igrave;nh C# với Winform trị gi&aacute; 600K 8\" src=\"https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled.png\" sizes=\"(max-width: 627px) 100vw, 627px\" srcset=\"https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled.png 1600w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-300x300.png 300w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-1024x1024.png 1024w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-150x150.png 150w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-768x768.png 768w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-1536x1536.png 1536w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-75x75.png 75w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-750x750.png 750w, https://anonyviet.com/wp-content/uploads/2018/01/c-sharp-logo-filled-1140x1140.png 1140w\" alt=\"Share kh&oacute;a học lập tr&igrave;nh C# trị gi&aacute; 600K\" width=\"627\" height=\"627\" loading=\"lazy\" data-pin-no-hover=\"true\" /></p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Bồi dưỡng khả năng chạy thử, gỡ lỗi v&agrave; kiểm tra t&iacute;nh hợp lệ của chương tr&igrave;nh. S&acirc;u hơn nữa, bạn được hướng dẫn c&aacute;ch kết nối v&agrave; tương t&aacute;c với cơ sở dữ liệu (database) Microsoft SQL Server v&agrave; triển khai được m&ocirc; h&igrave;nh đa tầng trong C#.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Cuối c&ugrave;ng, bạn sẽ được chao chuốt kĩ năng kiểm thử. Đ&oacute;ng g&oacute;i v&agrave; xuất bản phần mềm một c&aacute;ch chuy&ecirc;n nghiệp v&agrave; c&aacute;ch l&agrave;m h&agrave;i l&ograve;ng kh&aacute;ch h&agrave;ng.</p>\r\n<h2 id=\"ftoc-tong-hop-muc-tieu-dat-duoc-sau-khoa-hoc\" class=\"ftwp-heading\" style=\"box-sizing: border-box; margin: 0.83em 0px 0.5em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 500; font-stretch: inherit; font-size: 1.953em; line-height: 1.35; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #4285f4; text-rendering: optimizelegibility; background-color: #ffffff;\"><span style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">Tổng hợp mục ti&ecirc;u đạt được sau kh&oacute;a học</span></h2>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Một nền tảng kiến thức to&agrave;n diện về ng&ocirc;n ngữ C#.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Hiểu r&otilde; v&agrave; x&acirc;y dựng tốt kiến thức đa tầng.</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">Khả năng ph&aacute;t triển phần mềm tương t&aacute;c động với cơ sở dữ liệu (database).</p>\r\n<div class=\"code-block code-block-6 ai-viewport-1 ai-viewport-2\" style=\"box-sizing: border-box; margin: 8px 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; color: #333333; background-color: #ffffff; clear: both; display: inherit !important;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\" align=\"center\">\r\n<div id=\"passback_pc_article_mid1_728x90//300x250//336x280\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\"></div>\r\n</div>\r\n</div>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 1.25em; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 16px; line-height: inherit; font-family: Roboto, Helvetica, Arial, sans-serif; vertical-align: baseline; text-rendering: optimizelegibility; color: #333333; background-color: #ffffff;\">C&oacute; đủ kiến thức để học tiếp lập tr&igrave;nh di động đa nền tảng với Xamarin.</p>'),
(3, 'Lập trình Java', 'phòng lab', 40, '20h-22h00 thứ 5 và chủ nhật ', 900000, 'java.jpg', '<p><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; background-color: #ffffff; font-size: 14px; line-height: normal;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">Kh&oacute;a học lập tr&igrave;nh java căn bản tại Stanford sẽ hướng dẫn c&aacute;c bạn từ đơn giản đến phức tạp nhất trong lập tr&igrave;nh Java l&agrave; bước đệm để bạn học tiếp tới n&acirc;ng cao</strong></span><br style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" /><br style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" /></p>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">DEVZONE &ndash; dạy kinh nghiệm lập tr&igrave;nh</span></strong>&nbsp;đ&atilde; v&agrave; đang khẳng định vị tr&iacute; vững chắc của m&igrave;nh trong hoạt động gi&aacute;o dục CNTT tại Việt Nam, g&oacute;p phần đ&agrave;o tạo cho nguồn nh&acirc;n lực quốc gia h&agrave;ng trăm ngh&igrave;n nh&acirc;n sự CNTT chất lượng cao.</span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Kh&oacute;a học lập tr&igrave;nh Java&nbsp;</span></strong><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">l&agrave; một trong những kh&oacute;a học nổi bật v&agrave; được nhiều bạn sinh vi&ecirc;n lựa chọn khi theo học tại Stanford. V&acirc;y Java l&agrave; g&igrave;? học Java c&oacute; kh&oacute; kh&ocirc;ng l&agrave; những c&acirc;u hỏi m&agrave; bất kỳ ai cũng quan t&acirc;m khi đang muốn lựa chọn ng&agrave;nh học n&agrave;y.</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Java</span></strong><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">&nbsp;l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh do h&atilde;ng Sun Microsystem giới thiệu v&agrave;o năm 1995, Java l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh cấp cơ sở nhưng n&oacute; được thiết kế sao cho c&oacute; thể d&ugrave;ng như một hệ điều h&agrave;nh m&aacute;y t&iacute;nh đa năng &ndash; hay n&oacute;i c&aacute;ch kh&aacute;c n&oacute; rất c&oacute; triển vọng trở th&agrave;nh một lựa chọn kh&aacute;c thay cho Windows. Ch&iacute;nh v&igrave; Java c&oacute; thể chạy tr&ecirc;n nhiều hệ thống m&aacute;y t&iacute;nh kh&aacute;c nhau m&agrave; người ta dự định rằng n&oacute; sẽ gi&uacute;p khắc phục những lỗi tương th&iacute;ch vớ vẩn xảy ra trong ng&agrave;nh c&ocirc;ng nghiệp phần mềm.</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"center\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\"><br style=\"margin: 0px; padding: 0px; box-sizing: border-box;\" /><span class=\"resizebox gensmall clearfix\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; cursor: default; display: inline-block; font-size: 15px; line-height: 1.1em; max-width: 100%; text-align: left; width: 600px;\"><span class=\"resize_border clearfix\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: 1px solid #ffffff; display: block;\"><span class=\"resize_content clearfix\" style=\"margin: 0px; padding: 5px 10px; box-sizing: border-box; display: inline; zoom: 1;\"><a class=\"enlarge\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: #0499e3; text-decoration-line: none; display: inline; zoom: 1;\" href=\"https://raovat.4umer.com/t73719-topic\">Enlarge this image</a><span class=\"resize_filler\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; display: inline; width: 40px; zoom: 1;\">&nbsp;</span><a class=\"fullsize\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: #0499e3; text-decoration-line: none;\" href=\"https://raovat.4umer.com/t73719-topic\">Click to see fullsize</a></span></span></span><br style=\"margin: 0px; padding: 0px; box-sizing: border-box;\" /><img class=\"resize_img\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border-width: 0px; max-width: 100%;\" src=\"https://images-blogger-opensocial.googleusercontent.com/gadgets/proxy?url=http%3A%2F%2Fstanford.com.vn%2FPortals%2F0%2FImages%2FSEO4%2Fkhoa-hoc-lap-trinh-java.JPG%3Fver%3D2016-10-03-153501-833&amp;container=blogger&amp;gadget=a&amp;rewriteMime=image%2F*\" alt=\"Kh&oacute;a học lập tr&igrave;nh Java cơ bản cho người mới bắt đầu Proxy?url=http%3A%2F%2Fstanford.com.vn%2FPortals%2F0%2FImages%2FSEO4%2Fkhoa-hoc-lap-trinh-java\" width=\"600\" height=\"450\" /></span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Java được giới thiệu l&agrave; &ldquo;Chỉ viết một lần, chạy được khắp nơi&rdquo;. Th&ecirc;m v&agrave;o đ&oacute; Java c&ograve;n gi&uacute;p người lập tr&igrave;nh kh&ocirc;ng phải lo lắng nhiều về vấn đề bảo mật cho m&aacute;y t&iacute;nh của m&igrave;nh. V&igrave; Java c&oacute; thể chạy gần như tr&ecirc;n mọi hệ thống n&ecirc;n kh&ocirc;ng nhất thiết cần đến kh&aacute;i niệm li&ecirc;n kết mở rộng, vốn l&agrave; điểm yếu của to&agrave;n mạng.</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">C&aacute;c bạn c&oacute; thể thấy mọi nơi xung quanh ch&uacute;ng ta, Java xuất hiện từ desktop đến web v&agrave; mobile, c&aacute;c ứng dụng t&agrave;i ch&iacute;nh cho đến c&aacute;c ứng dụng android, từ những trang web thương mại điện tử hay c&aacute;c tr&ograve; chơi tr&ecirc;n điện thoại&hellip;Sự phổ biến của ng&ocirc;n ngữ n&agrave;y đ&atilde; l&agrave;m nảy sinh nhu cầu nguồn lực lập tr&igrave;nh vi&ecirc;n Java kh&ocirc;ng hề nhỏ cho c&aacute;c c&ocirc;ng ty phần mềm tr&ecirc;n thế giới cũng như ở Việt Nam. Điều đ&oacute; cho thấy, cơ hội việc l&agrave;m cho những ai đ&atilde; tham gia&nbsp;<strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">kh&oacute;a học lập tr&igrave;nh Java cơ bản</strong>&nbsp;tại Stanford l&agrave; cực kỳ lớn nếu bạn quyết t&acirc;m theo đuổi ng&agrave;nh học th&uacute; vị n&agrave;y!</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Sau khi ho&agrave;n tất kh&oacute;a học học vi&ecirc;n sẽ được trang bị kiến thức vững chắc về ng&ocirc;n ngữ lập tr&igrave;nh JAVA. Từ đ&oacute; học vi&ecirc;n c&oacute; thể tự nghi&ecirc;n cứu v&agrave; ph&aacute;t triển th&ecirc;m kỹ năng lập tr&igrave;nh JAVA v&agrave; tiến xa hơn l&agrave; sử dụng JAVA để ph&aacute;t triển c&aacute;c ứng dụng phần mềm, ứng dụng web, ứng dụng mobile (Android).</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Ngo&agrave;i ra trong suốt kh&oacute;a học n&agrave;y, học vi&ecirc;n sẽ được hướng dẫn c&aacute;c bước để x&acirc;y dựng một ứng dụng quản l&yacute; s&aacute;ch ho&agrave;n chỉnh. Th&ocirc;ng qua việc x&acirc;y dựng ứng dụng học vi&ecirc;n sẽ biết v&agrave; vận dụng được c&aacute;c kiến thức được học v&agrave;o c&ocirc;ng việc thực tế.</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"center\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\"><br style=\"margin: 0px; padding: 0px; box-sizing: border-box;\" /><span class=\"resizebox gensmall clearfix\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; cursor: default; display: inline-block; font-size: 15px; line-height: 1.1em; max-width: 100%; text-align: left; width: 600px;\"><span class=\"resize_border clearfix\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: 1px solid #ffffff; display: block;\"><span class=\"resize_content clearfix\" style=\"margin: 0px; padding: 5px 10px; box-sizing: border-box; display: inline; zoom: 1;\"><a class=\"enlarge\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: #0499e3; text-decoration-line: none; display: inline; zoom: 1;\" href=\"https://raovat.4umer.com/t73719-topic\">Enlarge this image</a><span class=\"resize_filler\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border: none; display: inline; width: 40px; zoom: 1;\">&nbsp;</span><a class=\"fullsize\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; color: #0499e3; text-decoration-line: none;\" href=\"https://raovat.4umer.com/t73719-topic\">Click to see fullsize</a></span></span></span><br style=\"margin: 0px; padding: 0px; box-sizing: border-box;\" /><img class=\"resize_img\" style=\"margin: 0px; padding: 0px; box-sizing: border-box; border-width: 0px; max-width: 100%;\" src=\"https://images-blogger-opensocial.googleusercontent.com/gadgets/proxy?url=http%3A%2F%2Fstanford.com.vn%2FPortals%2F0%2FImages%2FSEO2%2Fkhoa-hoc-lap-trinh-java-3.JPG%3Fver%3D2016-07-12-104627-763&amp;container=blogger&amp;gadget=a&amp;rewriteMime=image%2F*\" alt=\"Kh&oacute;a học lập tr&igrave;nh Java cơ bản cho người mới bắt đầu Proxy?url=http%3A%2F%2Fstanford.com.vn%2FPortals%2F0%2FImages%2FSEO2%2Fkhoa-hoc-lap-trinh-java-3\" width=\"600\" height=\"450\" /></span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">Kh&oacute;a học lập tr&igrave;nh Java tại Stanford</span></strong><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: inherit;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">&nbsp;mang tới bạn điều g&igrave;?</span></span></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Nền tảng vững chắc về lập tr&igrave;nh Java cơ bản</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- R&egrave;n luyện tư duy lập tr&igrave;nh v&agrave; tư duy giải quyết vấn đề trong thực tế</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Nắm vững c&aacute;c kỹ thuật thao t&aacute;c với Array v&agrave; String trong thực tế</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Nắm vững kiến thức của lập tr&igrave;nh hướng đối tượng</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Sử dụng linh hoạt c&aacute;c collecton trong vấn đề lưu trữ danh s&aacute;ch</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Biết c&aacute;ch vận dụng c&aacute;c kiến thức đ&atilde; học trong việc x&acirc;y dựng ứng dụng trong thực tế</span></em></span></div>\r\n<div style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-family: Roboto, arial, sans-serif; font-size: 15px; background-color: #ffffff;\" align=\"justify\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 14px; line-height: normal;\"><em style=\"margin: 0px; padding: 0px; box-sizing: border-box;\"><span style=\"margin: 0px; padding: 0px; box-sizing: border-box; font-size: 13px; line-height: normal;\">- Dễ d&agrave;ng chinh phục lập tr&igrave;nh ứng dụng Android</span></em></span></div>'),
(4, 'Lập trình Python', 'trực tuyến', 21, '18h30-21h thứ 2 và thứ 6 hàng tuần', 3250000, 'python.jpg', '<h2 dir=\"ltr\" style=\"margin: 17.7px 0px; line-height: 21px; font-weight: 400; font-size: 14px; color: #333333; font-family: Arial, Helvetica, sans-serif; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; color: #a52a2a;\"><strong style=\"margin: 0px;\"><span style=\"margin: 0px; font-size: 20px;\">Python l&agrave; g&igrave;?</span></strong></span></h2>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\"><strong style=\"margin: 0px;\">Ng&ocirc;n ngữ Python</strong>&nbsp;hiện đang l&agrave; xu hướng của thế giới với cộng đồng hỗ trợ mạnh mẽ, thư viện phong ph&uacute;. Ch&iacute;nh v&igrave; vậy m&agrave; nhiều người khi c&oacute; &yacute; định theo đuổi nghề lập tr&igrave;nh sẽ t&igrave;m hiểu v&agrave; lựa chọn Python l&agrave;m nền tảng kiến thức đầu ti&ecirc;n. Bạn chắc chắn sẽ đi trước thời đại với ng&ocirc;n ngữ Python &ndash; loại ng&ocirc;n ngữ cực k&igrave; dễ đọc v&agrave; dễ học.</span></p>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\"><strong style=\"margin: 0px;\">Python</strong>&nbsp;l&agrave; một ng&ocirc;n ngữ lập tr&igrave;nh đang ph&aacute;t triển v&agrave; n&oacute; kh&ocirc;ng c&oacute; dấu hiệu sẽ chậm lại trong v&agrave;i năm tiếp theo. Hầu như, c&oacute; thể dễ d&agrave;ng bắt gặp Python trong hầu như&nbsp;<em style=\"margin: 0px;\">mọi web applications, desktop apps, network servers, machine learning, media tool,&hellip;</em></span></p>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\">Bạn c&oacute; thể thấy rằng<strong style=\"margin: 0px;\">&nbsp;Python</strong>&nbsp;c&ograve;n cực k&igrave; hữu &iacute;ch cho c&aacute;c dịch vụ back-end v&agrave; API. Ngo&agrave;i ra, những framework dựa tr&ecirc;n Python như Django, Pyramid v&agrave; Turbo Gear cũng ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến hơn.</span></p>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: center;\"><img style=\"margin: 0px; border: 0px; max-width: 100%; height: auto !important;\" src=\"http://aptechsaigon.edu.vn/gw-content/images/1571796033-21efmm5.png\" alt=\"lap-trinh-ngon-ngu-python\" /></p>\r\n<h2 dir=\"ltr\" style=\"margin: 17.7px 0px; line-height: 21px; font-weight: 400; font-size: 14px; color: #333333; font-family: Arial, Helvetica, sans-serif; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; color: #a52a2a;\"><strong style=\"margin: 0px;\"><span style=\"margin: 0px; font-size: 20px;\">Tại sao chọn kh&oacute;a học lập tr&igrave;nh&nbsp;Python tại KASE?</span></strong></span></h2>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\"><strong style=\"margin: 0px;\">KASE Edutech</strong>&nbsp;l&agrave; một trong những cơ sở đ&agrave;o tạo CNTT uy t&iacute;n h&agrave;ng đầu tại TP. HCM, tự tin sẽ mang đến những&nbsp;<strong style=\"margin: 0px;\">kh&oacute;a học lập tr&igrave;nh</strong>&nbsp;chất lượng nhất d&agrave;nh cho bạn. Lập tr&igrave;nh&nbsp;<strong style=\"margin: 0px;\">Python&nbsp;</strong>l&agrave; kh&oacute;a học ngắn hạn cho học sinh tốt nghiệp PTTH, sinh vi&ecirc;n c&aacute;c trường Cao đẳng, Đại học v&agrave; những người l&agrave;m trong lĩnh vực CNTT c&oacute; định hướng sẽ lập tr&igrave;nh bằng ng&ocirc;n ngữ&nbsp;<strong style=\"margin: 0px;\">Python</strong>.&nbsp;</span></p>\r\n<p style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\">Chương tr&igrave;nh học lập tr&igrave;nh Python tại&nbsp;<strong style=\"margin: 0px;\">KASE</strong>&nbsp;được x&acirc;y dựng dựa tr&ecirc;n việc ứng dụng phương ph&aacute;p thực h&agrave;nh chuy&ecirc;n s&acirc;u v&agrave; đồ &aacute;n thực tế. C&aacute;c học vi&ecirc;n kh&ocirc;ng chỉ được cung c&acirc;́p trọn b&ocirc;̣ ki&ecirc;́n thức từ cơ bản của&nbsp;<strong style=\"margin: 0px;\">l&acirc;̣p trình Python</strong>, m&agrave; c&ograve;n có th&ecirc;̉ tạo ra m&ocirc;̣t&nbsp;<strong style=\"margin: 0px;\">ứng dụng Python</strong>&nbsp;hoàn chỉnh sau khi hoàn thành khóa học.</span></p>\r\n<p style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\">Thời gian học linh động bao gồm h&igrave;nh thức<strong style=\"margin: 0px;\">&nbsp;online &amp; offline</strong>. Cam kết đảm bảo nền&nbsp;tảng kiến thức cho học vi&ecirc;n.</span></p>\r\n<p style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: center;\"><img style=\"margin: 0px; border: 0px; max-width: 100%; height: auto !important;\" src=\"https://aptechsaigon.edu.vn/gw-content/images/1612255604-IgR6ABI.jpg\" alt=\"khoa lap trinh python online\" /></p>\r\n<h3 dir=\"ltr\" style=\"margin: 17.7px 0px; line-height: 21px; font-weight: 400; font-size: 14px; color: #333333; font-family: Arial, Helvetica, sans-serif; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px;\"><span style=\"margin: 0px; color: #b22222;\"><em style=\"margin: 0px;\"><strong style=\"margin: 0px;\">Với th&acirc;m ni&ecirc;n 20 năm hoạt động tại Việt Nam, KASE xin cam kết:&nbsp;</strong></em></span></span></h3>\r\n<ul style=\"margin: 0px 0px 0px 30px; list-style-position: outside; padding: 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff;\">\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Mang đến chương tr&igrave;nh đ&agrave;o tạo lập tr&igrave;nh vi&ecirc;n&nbsp;đầy t&iacute;nh thực tiễn, c&oacute; chiều s&acirc;u, dễ hiểu, t&iacute;nh khả thi cao, tr&aacute;nh h&agrave;n l&acirc;m l&yacute; thuyết nhằm mang lại kết quả tốt nhất cho học vi&ecirc;n.</span></p>\r\n</li>\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Trang bị đầy đủ thiết bị, cơ sở vật chất ti&ecirc;n tiến đ&aacute;p ứng mọi nhu cầu trong học tập</span></p>\r\n</li>\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Đội ngũ giảng vi&ecirc;n&nbsp;<strong style=\"margin: 0px;\">KASE</strong>&nbsp;l&agrave;&nbsp;những nh&agrave; lập tr&igrave;nh chuy&ecirc;n m&ocirc;n cao, nhiều kinh nghiệm thực tiễn tại c&aacute;c c&ocirc;ng ty, tập đo&agrave;n lớn.&nbsp;</span></p>\r\n</li>\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Hỗ trợ sắp xếp linh hoạt thời gian biểu c&aacute;c m&ocirc;n cho học vi&ecirc;n</span></p>\r\n</li>\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\"><strong style=\"margin: 0px;\">KASE</strong>&nbsp;sẽ cấp chứng chỉ c&ocirc;ng nhận cho học vi&ecirc;n sau khi ho&agrave;n th&agrave;nh kh&oacute;a học</span></p>\r\n</li>\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Cung cấp trọn bộ kiến thức về ng&ocirc;n ngữ l&acirc;̣p trình Python (<em style=\"margin: 0px;\">từ định nghĩa đ&ecirc;́n ki&ecirc;̉u dữ li&ecirc;̣u, bi&ecirc;́n, c&acirc;u l&ecirc;̣nh, mảng, chu&ocirc;̃i cho đến c&acirc;́u trúc đi&ecirc;̀u khi&ecirc;̉n, cấu tr&uacute;c v&ograve;ng lặp trong&nbsp;<strong style=\"margin: 0px;\">Python</strong>....</em>), tạo cơ h&ocirc;̣i cho học vi&ecirc;n thực hành tạo&nbsp;<strong style=\"margin: 0px;\">ứng dụng Python</strong>&nbsp;trong qu&aacute; tr&igrave;nh học.</span></p>\r\n</li>\r\n</ul>\r\n<p dir=\"ltr\" style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff; text-align: center;\"><iframe style=\"margin: auto; width: 600px; max-width: 600px; height: 400px;\" src=\"https://www.youtube.com/embed/AGI4JlD1V6o\" frameborder=\"0\" scrolling=\"no\"></iframe></p>\r\n<ul style=\"margin: 0px 0px 0px 30px; list-style-position: outside; padding: 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff;\">\r\n<li dir=\"ltr\" style=\"margin: 8.55px 0px; padding: 0px;\">\r\n<p dir=\"ltr\" style=\"margin: 17.1px 0px; text-align: justify;\"><span style=\"margin: 0px;\">Kết th&uacute;c kh&oacute;a học, học vi&ecirc;n sẽ c&oacute; đủ nền tảng kiến thức để tiếp tục theo học c&aacute;c m&ocirc;n:&nbsp;<em style=\"margin: 0px;\">Cấu tr&uacute;c dữ liệu, lập tr&igrave;nh Kotlin, lập tr&igrave;nh Java, lập tr&igrave;nh Android, Web...</em></span></p>\r\n</li>\r\n</ul>\r\n<p style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff;\">Hiện tại&nbsp;l&agrave; thời điểm l&yacute; tưởng v&agrave; th&iacute;ch hợp để bạn lựa chọn cho m&igrave;nh một&nbsp;<strong style=\"margin: 0px;\">kh&oacute;a học lập tr&igrave;nh</strong>. Với thời gian biểu ph&ugrave; hợp, bạn sẽ dễ d&agrave;ng hơn trong việc sắp xếp thời gian học tập tr&ecirc;n trường v&agrave; Trung t&acirc;m.&nbsp;<strong style=\"margin: 0px;\">Kh&oacute;a Lập tr&igrave;nh Python</strong>&nbsp;kh&ocirc;ng những gi&uacute;p bạn trang bị kiến thức cần thiết m&agrave; c&ograve;n c&oacute; cơ hội ph&aacute;t triển tr&ecirc;n con đường trở th&agrave;nh một l<strong style=\"margin: 0px;\">ập tr&igrave;nh vi&ecirc;n chuy&ecirc;n nghiệp</strong></p>\r\n<p style=\"margin: 17.7px 0px; color: #333333; font-family: Arial, Helvetica, sans-serif; font-size: 14px; background-color: #ffffff;\">&nbsp;</p>');
INSERT INTO `tbl_khoa_hoc` (`khoa_hoc_id`, `ten_khoa_hoc`, `hinh_thuc`, `buoi_hoc`, `thoi_gian`, `gia`, `anh_khoa_hoc`, `mo_ta`) VALUES
(6, 'Lập trình Android', 'phòng lab', 42, '18h45-20h thứ 2, thứ 4 và thứ 6', 4800000, 'android.jpg', '<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 16px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Kh&oacute;a học lập tr&igrave;nh Android được tổ chức dựa tr&ecirc;n sự hợp t&aacute;c giữa học viện Quốc tế đ&agrave;o tạo CNTT NIIT-ICT H&agrave; Nội v&agrave; c&aacute;c doanh nghiệp. Kh&oacute;a học đ&agrave;o tạo theo đơn đặt h&agrave;ng nh&acirc;n sự từ doanh nghiệp.</span></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">1. Tổng quan về kh&oacute;a học Android</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><a style=\"margin: 0px; padding: 0px; line-height: 1.6em; text-decoration-line: none;\" href=\"http://niithanoi.vn/chuong-trinh-dao-tao/365/lap-trinh-android.html\" target=\"_blank\" rel=\"noopener\">Kh&oacute;a học Lập tr&igrave;nh Android</a></strong>&nbsp;được&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">NIIT - ICT H&agrave; Nội</strong>&nbsp;nghi&ecirc;n cứu thực tế y&ecirc;u cầu c&ocirc;ng việc của một Lập tr&igrave;nh vi&ecirc;n Android v&agrave; bi&ecirc;n soạn theo Chuẩn Quốc tế.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;l&agrave; hệ điều h&agrave;nh tr&ecirc;n thiết bị di động (điện thoại, m&aacute;y t&iacute;nh bảng, TV, c&aacute;c thiết bị di động kh&aacute;c&hellip;) được phổ biến v&agrave; ưa chuộng nhất hiện nay.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />Kể từ khi được Google c&ocirc;ng bố v&agrave;o th&aacute;ng 10/2008, Hệ điều h&agrave;nh&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;đ&atilde; c&oacute; những bước ph&aacute;t triển ngoạn mục, tới nay hệ điều h&agrave;nh&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;đ&atilde; trở th&agrave;nh hệ điều h&agrave;nh được y&ecirc;u th&iacute;ch nhất với số lượng thiết bị di động sử dụng nhiều nhất so với c&aacute;c hệ điều h&agrave;nh kh&aacute;c.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />Hệ điều h&agrave;nh Android&nbsp;c&oacute; nhiều ưu thế vượt trội như: HĐH m&atilde; nguồn mở, Ứng dụng được ph&aacute;t triển dựa tr&ecirc;n nền tảng Java, hỗ trợ nhiều vi xử l&yacute; ngo&agrave;i thiết bị di động.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Với tr&ecirc;n 148 triệu thu&ecirc; bao di động, trong đ&oacute; 60% l&agrave; giới trẻ, thị trường ph&aacute;t triển ứng dụng cho thiết bị di động, đặc biệt l&agrave; tr&ecirc;n nền Android hứa hẹn đầy tiềm năng tại Việt Nam. Trong xu thế ph&aacute;t triển đ&oacute;, nhu cầu về&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">đ&agrave;o tạo lập tr&igrave;nh Android</strong>&nbsp;ng&agrave;y c&agrave;ng cấp b&aacute;ch v&agrave; tăng mạnh.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">NIIT - ICT H&Agrave; NỘI</strong>&nbsp;phối hợp với doanh nghiệp thiết kế chương tr&igrave;nh&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Đ&agrave;o tạo lập tr&igrave;nh Android</strong>&nbsp;với mục đ&iacute;ch:<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Gi&uacute;p học vi&ecirc;n nắm được quy tr&igrave;nh ph&aacute;t triển một ứng dụng&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android&nbsp;</strong>theo những quy tr&igrave;nh ph&aacute;t triển chuẩn từ doanh nghiệp. Từ đ&oacute;, học vi&ecirc;n c&oacute; thể tự m&igrave;nh biến c&aacute;c &yacute; tưởng của m&igrave;nh th&agrave;nh c&aacute;c ứng dụng&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />-&nbsp;Học vi&ecirc;n sẽ được trang bị những kiến thức v&agrave; kỹ năng cần thiết để tự tin apply v&agrave;o c&aacute;c doanh nghiệp phần mềm.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px; height: 242px; width: 660px;\" src=\"http://icthanoi.edu.vn/images/2015/03/25/dao-tao-lap-trinh-android.jpg\" alt=\"kh&oacute;a học lập tr&igrave;nh android\" /></span></p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff; text-align: center;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><a style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; text-decoration-line: none;\" href=\"http://icthanoi.edu.vn/khoa-hoc-lap-trinh-android-115.htm\" target=\"_blank\" rel=\"noopener\">Học lập tr&igrave;nh Android</a></span></strong></h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">2. Thời lượng - Học ph&iacute;</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp; - Thời lượng: 54 giờ, 27 buổi.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + 25 buổi học l&iacute; thuyết v&agrave; thực h&agrave;nh.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + 1 buổi thi, Tư vấn việc l&agrave;m (Viết CV, Kinh nghiệm phỏng vấn).</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; + 1 buổi bảo vệ đề t&agrave;i (Bảo vệ dự &aacute;n).</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp; - Học ph&iacute;: Li&ecirc;n hệ</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp;&nbsp; - Lịch học: Học vi&ecirc;n c&oacute; thể lựa chọn c&aacute;c ca học: 2-4-6 hoặc 3-5-7 .</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; C&aacute;c khung giờ: 17:30 &ndash; 19:30 hoặc 19:30 &ndash; 21:30.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">3. Giảng vi&ecirc;n.</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp;&nbsp;&nbsp; L&agrave; Team Leader, Senior, Quản trị dự &aacute;n c&oacute; nhiều kinh nghiệm, đến từ c&aacute;c doanh nghiệp phần mềm lớn.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">4. Y&ecirc;u cầu kiến thức đầu v&agrave;o</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp; &nbsp;- Học vi&ecirc;n phải c&oacute; kiến thức cơ bản về Java. (Trong qu&aacute; tr&igrave;nh học sẽ được hỗ trợ bổ sung Java).</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp; &nbsp;- C&oacute; kiến thức về: Lập tr&igrave;nh hướng đối tượng (OOP).</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp; &nbsp;- Tiếng anh tốt l&agrave; một lợi thế.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&gt;&gt; Tự chuẩn bị kiến thức trước khi học Android với b&agrave;i hướng dẫn&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><a style=\"margin: 0px; padding: 0px; line-height: 1.6em; text-decoration-line: none;\" href=\"https://niithanoi.edu.vn/lap-trinh-java.html\">Tự học Lập tr&igrave;nh Java</a></strong>&nbsp;(One for All) ngay!</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">5. Nội dung kh&oacute;a học:</span></strong><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px;\" src=\"http://icthanoi.edu.vn/images/2017/04/18/khoa-hoc-lap-trinh-android-1.jpg\" alt=\"Kho&aacute; học lập tr&igrave;nh android niit\" /><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px;\" src=\"http://icthanoi.edu.vn/images/2017/04/18/khoa-hoc-lap-trinh-android.jpg\" alt=\"kho&aacute; học lập tr&igrave;h android - icthanoi.edu.vn\" /></span></p>\r\n<h3 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Nội dung&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Kho&aacute; học lập tr&igrave;nh Android</strong></span></h3>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">6. Lợi thế của kh&oacute;a học</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp;&nbsp; - Ph&ograve;ng Lab hiện đại, đầy đủ tiện nghi với hệ thống m&aacute;y t&iacute;nh cấu h&igrave;nh cao. Học vi&ecirc;n được sử dụng thiết bị smart phone&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;thật để thực h&agrave;nh.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; &nbsp;- Học vi&ecirc;n được học tập với sự hướng dẫn của những giảng vi&ecirc;n c&oacute; nhiều kinh nghiệm đến từ doanh nghiệp, được thực h&agrave;nh qua c&aacute;c dự &aacute;n thực tế của doanh nghiệp.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Học vi&ecirc;n được hỗ trợ tham gia c&aacute;c kỳ thi chuy&ecirc;n m&ocirc;n cao như: Khởi nghiệp do VCCI tổ chức.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Giảng vi&ecirc;n sẽ hỗ trợ học vi&ecirc;n tối đa trong kh&oacute;a học, v&agrave; sau kh&oacute;a học sẽ c&oacute; sự hỗ trợ, tư vấn trong c&ocirc;ng việc thực tế.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;&nbsp;&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Cuối kh&oacute;a học, Học vi&ecirc;n sẽ được NIIT-ICT H&Agrave; NỘI hỗ trợ giới thiệu việc l&agrave;m tới c&aacute;c đối t&aacute;c doanh nghiệp li&ecirc;n kết với NIIT-ICT H&Agrave; NỘI</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">7. Khả năng sau kh&oacute;a học</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - Sử dụng th&agrave;nh thạo c&aacute;c c&ocirc;ng cụ lập tr&igrave;nh như IDE (Eclipse, Android Studio), Source Control (SVN, Git), Team (Skype, MailGroup,&hellip;)</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - Hiểu r&otilde; quy tr&igrave;nh ph&aacute;t triển ứng dụng di động&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - L&agrave;m việc với c&aacute;c dự &aacute;n lớn về&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Android</strong>&nbsp;tại c&aacute;c doanh nghiệp phần mềm lớn trong nước v&agrave; nước ngo&agrave;i.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - L&agrave;m việc với c&aacute;c thư viện ngo&agrave;i, SDK lớn: Youtube, Facebook, Google Plus, Google Map, Vitamio, Volley, Image_loader_universal,..</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - Tự ph&aacute;t triển được c&aacute;c ứng dụng, game Android: T&iacute;ch hợp c&aacute;c hệ thống quảng c&aacute;o Google Admod, Start App,.. để kiếm doanh thu, hoặc tự đẩy c&aacute;c app, game l&ecirc;n c&aacute;c store để kinh doanh.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">&nbsp; - Sản phẩm c&oacute; thể l&agrave;m:</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px;\" src=\"http://icthanoi.edu.vn/images/2015/03/25/khoa-hoc-lap-trinh-android(2).png\" alt=\"học lập tr&igrave;nh android\" /></span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">8. Chứng chỉ</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Được NIIT-ICT H&Agrave; NỘI cấp c&oacute; gi&aacute; trị tr&ecirc;n to&agrave;n quốc.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">9. Ch&iacute;nh s&aacute;ch bảo h&agrave;nh học tập</span></strong><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Bảo h&agrave;nh học tập, tổ chức Đ&Agrave;O TẠO LẠI cho tất cả học vi&ecirc;n đ&atilde; theo học tại Học viện nhưng kết quả học tập chưa đạt y&ecirc;u cầu, hoặc cần hỗ trợ th&ecirc;m về kiến thức để l&agrave;m việc thực tế.</span></p>'),
(7, 'Lập trình web', 'trực tuyến', 54, '20h30-22h thứ 7 và chủ nhật', 4768000, 'web.jpg', '<h1 style=\"box-sizing: inherit; font-size: 36px; line-height: 38px; margin: 0px; padding: 0px; clear: both; font-family: Lato, sans-serif; background-color: #ffffff;\">Giới thiệu kh&oacute;a học lập tr&igrave;nh web</h1>\r\n<div class=\"entry-content\" style=\"box-sizing: inherit; margin: 0px; font-family: Lato, sans-serif; font-size: 16px; background-color: #ffffff;\">\r\n<p style=\"box-sizing: inherit; line-height: 25px; letter-spacing: 1px;\">N&oacute;i đến web th&igrave; c&oacute; rất nhiều ng&ocirc;n ngữ lập tr&igrave;nh để tạo dựng n&ecirc;n một trang web như: Java, ASP&hellip;&nbsp;<span id=\"more-382\" style=\"box-sizing: inherit;\"></span>Nhưng c&oacute; một ng&ocirc;n ngữ chỉ chuy&ecirc;n về web th&ocirc;i đ&oacute; l&agrave;&nbsp;<strong style=\"box-sizing: inherit;\">ng&ocirc;n ngữ lập tr&igrave;nh</strong>&nbsp;<strong style=\"box-sizing: inherit;\">PHP</strong>&nbsp;viết tắt của &ldquo;Hypertext Preprocessor&rdquo;<strong style=\"box-sizing: inherit;\">&nbsp;</strong>&nbsp;V&agrave; hiện nay hầu hết c&aacute;c trang web được viết bằng ng&ocirc;n ngữ PHP, ngay cả những trang web lớn&nbsp;wikipedia.com hay mạng x&atilde; hội nổi tiếng facebook.com cũng d&ugrave;ng PHP để thiết kế trang web.</p>\r\n<p style=\"box-sizing: inherit; line-height: 25px; letter-spacing: 1px;\"><img class=\"aligncenter wp-image-383\" style=\"box-sizing: inherit; border: 0px; height: auto; max-width: 100%; vertical-align: top; clear: both; display: block; margin-left: auto; margin-right: auto;\" src=\"http://hocdelam.info/wp-content/uploads/2016/10/php.png\" sizes=\"(max-width: 848px) 100vw, 848px\" srcset=\"http://hoclam.net/wp-content/uploads/2016/10/php.png 940w, http://hoclam.net/wp-content/uploads/2016/10/php-300x94.png 300w, http://hoclam.net/wp-content/uploads/2016/10/php-768x241.png 768w\" alt=\"lạp trinh web PHP\" width=\"848\" height=\"266\" /></p>\r\n<p style=\"box-sizing: inherit; line-height: 25px; letter-spacing: 1px;\">M&atilde; nguồn tiếng anh gọi l&agrave; source code của ng&ocirc;n ngữ lập tr&igrave;nh PHP l&agrave; bao gồm cấu tr&uacute;c lệnh. N&oacute; ra lệnh cho server l&agrave;m việc như: số người đang online, hiện tại l&agrave; bao nhi&ecirc;u giờ&hellip;</p>\r\n<p style=\"box-sizing: inherit; line-height: 25px; letter-spacing: 1px;\">Để tạo ra trang web một lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n nghiệp về web sẽ biết th&ecirc;m cơ bản về Mysql, HTML, Javascript, CSS những kh&aacute;i niệm v&agrave; sử dụng ch&uacute;ng rất đơn giản.</p>\r\n</div>\r\n<footer class=\"entry-footer\" style=\"box-sizing: inherit; margin-bottom: 50px; font-family: Lato, sans-serif; font-size: 16px; background-color: #ffffff;\"><span class=\"tags-links\" style=\"box-sizing: inherit;\">Tagged&nbsp;<a style=\"box-sizing: inherit; background-color: transparent; text-decoration-line: none; color: #000000;\" href=\"http://hoclam.net/tag/hoc-lap-trinh-web/\" rel=\"tag\">học lập tr&igrave;nh web</a>,&nbsp;<a style=\"box-sizing: inherit; background-color: transparent; text-decoration-line: none; color: #000000;\" href=\"http://hoclam.net/tag/hoc-php/\" rel=\"tag\">học php</a>,&nbsp;<a style=\"box-sizing: inherit; background-color: transparent; text-decoration-line: none; color: #000000;\" href=\"http://hoclam.net/tag/hoc-thiet-ke-web/\" rel=\"tag\">học thiết kế web</a></span></footer>');
INSERT INTO `tbl_khoa_hoc` (`khoa_hoc_id`, `ten_khoa_hoc`, `hinh_thuc`, `buoi_hoc`, `thoi_gian`, `gia`, `anh_khoa_hoc`, `mo_ta`) VALUES
(8, 'Lập trình PHP', 'trực tuyến và pòng lab', 42, '19h-22h thứ 3 và thứ 5', 4850000, 'php.jpg', '<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">1/ Tổng quan&nbsp;kh&oacute;a học lập tr&igrave;nh PHP d&agrave;nh cho bạn!</span></strong></h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP&nbsp;</strong>(Hypertext Preprocessor) l&agrave; ng&ocirc;n ngữ lập tr&igrave;nh script rất th&ocirc;ng dụng, được d&ugrave;ng để ph&aacute;t triển website v&agrave; c&aacute;c ứng dụng m&atilde; nguồn mở, hoạt động ở tr&ecirc;n m&aacute;y chủ.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px; width: 660px; height: 289px;\" src=\"http://icthanoi.edu.vn/images/2017/12/05/khoa-hoc-lap-trinh-php.jpg\" alt=\"kh&oacute;a học lập tr&igrave;nh php tại h&agrave; nội\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Học lập tr&igrave;nh PHP</strong></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Ng&ocirc;n ngữ&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Lập tr&igrave;nh PHP</strong>&nbsp;được tối ưu h&oacute;a cho c&aacute;c ứng dụng web, tốc độ nhanh, nhỏ gọn, c&uacute; ph&aacute;p giống C v&agrave; Java. PHP rất dễ học n&ecirc;n đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh ng&ocirc;n ngữ lập tr&igrave;nh web phổ biến nhất thế giới.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />Việc sử dụng Framework gi&uacute;p cho việc lập tr&igrave;nh trở l&ecirc;n nhanh v&agrave; hiệu quả hơn.C&aacute;c trang Web nổi tiếng như Websites like, Yahoo, Wikipedia, Facebook, Digg, Myspace đều được lập tr&igrave;nh bằng&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong>.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />Hiện nay,&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">hơn 90%</strong>&nbsp;c&aacute;c&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Website&nbsp;</strong>đều được ph&aacute;t triển dựa tr&ecirc;n ng&ocirc;n ngữ lập tr&igrave;nh&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong>. Nhu cầu nh&acirc;n lực về PHP&nbsp;cũng đang cực kỳ lớn.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />Ứng dụng web viết bằng PHP c&oacute; thể triển khai tr&ecirc;n hầu hết c&aacute;c chủng loại m&aacute;y chủ hiện c&oacute;, tr&ecirc;n nhiều hệ điều h&agrave;nh kh&aacute;c nhau (Windows, Linux,..) v&agrave; c&aacute;c phần cứng kh&aacute;c nhau.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong>&nbsp;c&oacute; thể được sử dụng kết hợp với nhiều hệ quản trị dữ liệu quan hệ kh&aacute;c nhau, như: MySQL, MS SQL Server,...<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong>&nbsp;được ph&acirc;n phối miễn ph&iacute; v&agrave; nh&oacute;m ph&aacute;t triển&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong>&nbsp;cung cấp c&aacute;c m&atilde; nguồn ho&agrave;n chỉnh để người d&ugrave;ng c&oacute; thể t&ugrave;y biến v&agrave; mở rộng theo mục đ&iacute;ch sử dụng ri&ecirc;ng.&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px; height: 400px; width: 660px;\" src=\"http://icthanoi.edu.vn/images/2015/03/28/khoa-hoc-lap-trinh-php-niithanoi.jpg\" alt=\"Kh&oacute;a học lập Tr&igrave;nh PHP Doanh Nghiệp 2\" /></span></p>\r\n<p><span style=\"font-family: arial, sans-serif; font-size: 13px; background-color: #ffffff;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><a style=\"margin: 0px; padding: 0px; line-height: 1.6em; text-decoration-line: none;\" href=\"https://niithanoi.edu.vn/lap-trinh-web-php.html\" target=\"_blank\" rel=\"noopener\">Kh&oacute;a học PHP</a></strong>&nbsp;được&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><a style=\"margin: 0px; padding: 0px; line-height: 1.6em; text-decoration-line: none;\" href=\"http://niithanoi.vn/\" target=\"_blank\" rel=\"noopener\">NIIT - ICT H&Agrave; NỘI</a></strong>&nbsp;phối hợp với doanh nghiệp thiết kế với mục đ&iacute;ch:<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />-&nbsp;Gi&uacute;p trang bị cho học vi&ecirc;n những kiến thức v&agrave; kỹ năng lập tr&igrave;nh bằng ng&ocirc;n ngữ PHP cần thiết nhất, gi&uacute;p học vi&ecirc;n nắm vững quy tr&igrave;nh ph&aacute;t triển ứng dụng m&agrave; c&aacute;c doanh nghiệp phần mềm lớn đang sử dụng, ph&aacute;t triển được c&aacute;c ứng dụng web theo quy tr&igrave;nh phần mềm chuẩn đ&aacute;p ứng được y&ecirc;u cầu c&ugrave;a kh&aacute;ch h&agrave;ng.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />-&nbsp;Học vi&ecirc;n c&oacute; cơ hội rộng mở để apply v&agrave;o nhiều doanh nghiệp li&ecirc;n kết với&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">NIIT - ICT H&Agrave; NỘI</strong>&nbsp;v&agrave; nhiều doanh nghiệp lớn kh&aacute;c.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff; text-align: justify;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">2.&nbsp;Thời lượng v&agrave; học ph&iacute;</span></strong></h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><em style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Thời lượng:</em>&nbsp;34 buổi - 98h (Tuần học 2 buổi, 3h/buổi). Kh&oacute;a học k&eacute;o d&agrave;i khoảng 5 th&aacute;ng</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><em style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Học ph&iacute;:</em>&nbsp;Li&ecirc;n hệ</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><em style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Lịch học:</em>&nbsp;</span><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">Tuần 2 buổi - 3h/buổi | C&aacute;c khung giờ: 18:00 &ndash; 21:00 hoặc 18:30 - 21:30</span></p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">3. Giảng vi&ecirc;n</span></strong><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;</h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">L&agrave; Team Leader, Quản trị dự &aacute;n c&oacute; nhiều kinh nghiệm&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">lập tr&igrave;nh PHP</strong>, đến từ c&aacute;c doanh nghiệp phần mềm lớn (DTT Group, Fsoft..)</span></p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">4. Y&ecirc;u cầu kiến thức đầu v&agrave;o</span></span></strong></h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Kh&ocirc;ng y&ecirc;u cầu kiến thức đầu v&agrave;o. Học vi&ecirc;n sẽ được đ&agrave;o tạo từ cơ bản đến n&acirc;ng cao - chuy&ecirc;n s&acirc;u.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- C&oacute; kiến thức cơ bản về lập tr&igrave;nh l&agrave; một lợi thế.</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Tiếng anh tốt l&agrave; một lợi thế.</span></p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" /><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\">5.&nbsp;Nội dung kho&aacute; học</span></strong><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;</h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><em style=\"margin: 0px; padding: 0px; line-height: 1.6em;\">C&aacute;c m&ocirc;n trong kho&aacute; học</em></strong></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- M&ocirc;n thứ nhất: Thiết kế responsive website với HTML 5 - CSS3 - JavaScript - Bootstrap. Thời lượng: 26h (8 buổi học +1 buổi thi)<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- M&ocirc;n thứ hai:&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Lập tr&igrave;nh web với PHP&amp;MySQL</strong>. Thời lượng: 35h (11 buổi học + 1 buổi thi)<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- M&ocirc;n thứ ba:&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">Lập tr&igrave;nh PHP chuy&ecirc;n s&acirc;u</strong>&nbsp;&amp; PHP Framework. Thời lượng: 20h (6 buổi học + 1 buổi thi)<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- M&ocirc;n thứ tư: Đồ &aacute;n cuối kh&oacute;a học. Học vi&ecirc;n ph&acirc;n nh&oacute;m đồ &aacute;n, v&agrave; ph&aacute;t triển đồ &aacute;n website thương mại điện tử sử dụng&nbsp;<span style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">&nbsp;</span><a style=\"margin: 0px; padding: 0px; line-height: 1.6em; text-decoration-line: none;\" href=\"http://icthanoi.edu.vn/\" target=\"_blank\" rel=\"noopener\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">ng&ocirc;n ngữ lập tr&igrave;nh PHP</strong></a>. Thời lượng: 18h (5 buổi hướng dẫn + 1 buổi bảo vệ)</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: justify;\">&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">---------</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><strong style=\"margin: 0px; padding: 0px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">NỘI DUNG KHO&Aacute; HỌC LẬP TR&Igrave;NH PHP DOANH NGHIỆP 4.0</span></strong></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 441px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-1.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 1\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 379px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-2.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 2\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 495px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-3.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 3\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 499px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-4.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 4\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 640px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-5.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 5\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><img style=\"margin: 0px; padding: 0px; line-height: 1.6em; border: 0px; width: 660px; height: 538px;\" src=\"http://icthanoi.edu.vn/images/2018/10/06/noi-dung-khoa-hoc-php-doanh-nghiep-4-0-phan-6.jpg\" alt=\"Nội dung kh&oacute;a học lập tr&igrave;nh PHP Doanh nghiệp 4.0 phần 5\" /></p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff; text-align: justify;\">&nbsp;</h2>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 18px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">6. Lợi thế của kh&oacute;a học</span></span></strong><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />&nbsp;</h2>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Ph&ograve;ng Lab hiện đại, đầy đủ tiện nghi với hệ thống m&aacute;y t&iacute;nh cấu h&igrave;nh cao.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Học vi&ecirc;n được học tập với sự hướng dẫn của những giảng vi&ecirc;n c&oacute; nhiều kinh nghiệm đến từ doanh nghiệp (Teamleader, Senior, Quản l&yacute; dự &aacute;n&hellip;), được thực h&agrave;nh qua c&aacute;c dự &aacute;n thực tế của doanh nghiệp.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Học vi&ecirc;n được tự tay lập tr&igrave;nh những Website thương mại điện tử, tham gia ph&aacute;t triển c&aacute;c dự &aacute;n web theo quy tr&igrave;nh ph&aacute;t triển phần mềm của doanh nghiệp.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Học vi&ecirc;n được hỗ trợ tham gia c&aacute;c kỳ thi chuy&ecirc;n m&ocirc;n cao như: Khởi nghiệp do VCCI tổ chức<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Giảng vi&ecirc;n sẽ hỗ trợ học vi&ecirc;n tối đa trong kh&oacute;a học, v&agrave; sau kh&oacute;a học sẽ c&oacute; sự hỗ trợ, tư vấn trong c&ocirc;ng việc thực tế.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Cuối kh&oacute;a học, Học vi&ecirc;n sẽ được NIIT - ICT H&Agrave; NỘI hỗ trợ giới thiệu việc l&agrave;m tới c&aacute;c đối t&aacute;c doanh nghiệp li&ecirc;n kết với NIIT-ICT H&Agrave; NỘI</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff; text-align: center;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><img style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em; border: 0px; width: 660px; height: 257px;\" src=\"http://icthanoi.edu.vn/images/2017/12/05/khoa-hoc-lap-trinh-php-3.jpg\" alt=\"đ&agrave;o tạo php đảm bảo việc l&agrave;m\" /></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">7.&nbsp;Khả năng sau kho&aacute; học</span></strong></h2>\r\n<p><span style=\"font-family: arial, sans-serif; font-size: 13px; background-color: #ffffff;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Th&agrave;nh thạo v&agrave; l&agrave;m chủ ng&ocirc;n ngữ lập tr&igrave;nh web phổ biến nhất hiện nay -&nbsp;<strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">PHP</strong><br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Sử dụng th&agrave;nh thạo một trong những hệ quản trị CSDL m&atilde; nguồn mở th&ocirc;ng dụng nhất hiện nay: MySQL, với c&ocirc;ng cụ quản trị Toad for MySQL, Navicat<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Hiểu r&otilde; c&aacute;c bước ph&aacute;t triển một website thương mại điện tử trong thực tế<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Th&agrave;nh thạo c&aacute;c kiến thức hiện đại trong thiết kế v&agrave; lập tr&igrave;nh web: HTML 5, JavaScript, CSS3, Jquery, Bootstrap, AJAX<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Nắm vững m&ocirc; h&igrave;nh MVC để ứng dụng v&agrave;o c&aacute;c dự &aacute;n thực tế<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Trải nghiệm qu&aacute; tr&igrave;nh ph&aacute;t triển đồ &aacute;n cuối kh&oacute;a học, để r&egrave;n luyện kỹ năng lập tr&igrave;nh v&agrave; kỹ năng l&agrave;m việc nh&oacute;m<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- L&agrave;m chủ một trong những PHP MVC Framework th&ocirc;ng dụng nhất hiện nay: CodeIgniter hoặc Laravel<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Sử dụng th&agrave;nh thạo c&aacute;c c&ocirc;ng cụ thiết kế v&agrave; lập tr&igrave;nh web như: DreamWeaver, SublimeText</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">8. Ưu điểm vượt trội của kho&aacute; học</span></strong></h2>\r\n<p><span style=\"font-family: arial, sans-serif; font-size: 13px; background-color: #ffffff;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\">- Học vi&ecirc;n được bảo h&agrave;nh Học tập l&acirc;u d&agrave;i: Sau khi học, nếu thấy chưa nắm được kiến thức, hoặc chưa tự tin để đi l&agrave;m, Học vi&ecirc;n sẽ được cho học dự th&iacute;nh với c&aacute;c lớp kh&oacute;a sau, cho đến khi th&agrave;nh thạo kiến thức.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Đội ngũ giảng vi&ecirc;n t&acirc;m huyết, nhiệt t&igrave;nh, sẵn s&agrave;ng hỗ trợ Học vi&ecirc;n online ngo&agrave;i giờ tr&ecirc;n lớp.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Ngo&agrave;i giờ học tr&ecirc;n lớp, Học vi&ecirc;n được đăng k&yacute; sử dụng ph&ograve;ng lab miễn ph&iacute; để thực h&agrave;nh, v&agrave; nếu chưa hiểu b&agrave;i c&oacute; thể l&ecirc;n Trung t&acirc;m đăng k&yacute; để được bổ trợ.<br style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\" />- Cuối kh&oacute;a học, Học vi&ecirc;n được giới thiệu việc l&agrave;m hoặc giới thiệu thực tập tại c&aacute;c doanh nghiệp.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" />&nbsp;</p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">9. Ch&iacute;nh s&aacute;ch bảo h&agrave;nh học tập</span></strong></h2>\r\n<p><span style=\"font-family: arial, sans-serif; font-size: 13px; background-color: #ffffff;\">&nbsp;</span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\"><span style=\"margin: 0px; padding: 0px; font-size: 14px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">- Bảo h&agrave;nh học tập, tổ chức Đ&Agrave;O TẠO LẠI cho tất cả học vi&ecirc;n đ&atilde; theo học tại Học viện nhưng kết quả học tập chưa đạt y&ecirc;u cầu, hoặc cần hỗ trợ th&ecirc;m về kiến thức để l&agrave;m việc thực tế.</span><br style=\"margin: 0px; padding: 0px; line-height: 1.6em;\" /><span style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\">- Học vi&ecirc;n theo học tại Học viện được hỗ trợ kiến thức, thực h&agrave;nh ngo&agrave;i giờ học offline (trực tiếp tại Học viện) v&agrave; online (trực tuyến với gi&aacute;o vi&ecirc;n) nhằm đảm bảo hiệu quả học tập tốt nhất cho học vi&ecirc;n.&nbsp;</span></span></p>\r\n<p style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: 13px; line-height: 1.6em; background-color: #ffffff;\">&nbsp;</p>\r\n<h2 style=\"margin: 0px; padding: 0px; font-family: arial, sans-serif; font-size: inherit; line-height: 1.6em; font-weight: inherit; background-color: #ffffff;\">&nbsp;</h2>\r\n<p><strong style=\"margin: 0px; padding: 0px; font-size: 13px; line-height: 1.6em;\"><span style=\"margin: 0px; padding: 0px; font-size: 20px; line-height: 1.6em;\">&nbsp;</span></strong></p>'),
(27, 'Lập trình game', 'trực tuyến', 10, '20h-22h50 thứ 7 và chủ nhật', 2500000, 'game.jpg', '<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\"><em>Ng&agrave;y nay, lập tr&igrave;nh game ng&agrave;y c&agrave;ng trở n&ecirc;n phổ biến v&agrave; nhận được sự thu h&uacute;t đ&ocirc;ng đảo của những người y&ecirc;u c&ocirc;ng nghệ. Game c&oacute; thể được tạo ra bằng nhiều ng&ocirc;n ngữ lập tr&igrave;nh kh&aacute;c nhau. Vậy để sớm th&agrave;nh c&ocirc;ng tr&ecirc;n con đường lập tr&igrave;nh game th&igrave; ch&uacute;ng ta phải bắt đầu từ đ&acirc;u v&agrave; học&nbsp;<span style=\"font-weight: bold;\">lập tr&igrave;nh game cần học những g&igrave;&nbsp;</span>để nhanh ra sản phẩm? H&atilde;y tham khảo ngay b&agrave;i viết n&agrave;y của MindX nh&eacute;!</em></p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: center;\"><img style=\"position: relative; max-width: 100%; height: 405px; width: 720px;\" src=\"https://resources.mindx.edu.vn/uploads/images/L%E1%BA%ADp%20tr%C3%ACnh%20game%20c%E1%BA%A7n%20h%E1%BB%8Dc%20nh%E1%BB%AFng%20g%C3%AC%20-%201.jpg\" alt=\"Lập tr&igrave;nh game cần học những g&igrave; - 1\" /></p>\r\n<h2 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\">1. Học lập tr&igrave;nh game kh&ocirc;ng hề kh&oacute; như bạn vẫn nghĩ!</h2>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Kh&ocirc;ng chỉ ri&ecirc;ng nghề lập tr&igrave;nh game m&agrave; nghề nghiệp n&agrave;o cũng c&oacute; những kh&oacute; khăn nhất định. Nhất l&agrave; những bạn mới bắt tay v&agrave;o học, chưa biết n&ecirc;n bắt đầu từ đ&acirc;u th&igrave; việc c&aacute;c bạn cảm thất lo lắng l&agrave; điều dễ hiểu.</p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Tuy nhi&ecirc;n, nếu bạn biết c&aacute;ch học, bạn ki&ecirc;n tr&igrave; v&agrave; chăm chỉ th&igrave; việc&nbsp;<a style=\"opacity: 1; text-decoration-line: none; transition: all 0.2s ease-in-out 0s;\" href=\"https://mindx.edu.vn/study-program/khoa-hoc-lap-trinh-tu-co-ban-toi-nang-cao?utm_source=Website&amp;utm_medium=Blog&amp;utm_campaign=Organic&amp;utm_content=5ea6482cfdfe6f18dcef6db8\"><u><span style=\"font-weight: bold;\">học lập tr&igrave;nh game</span></u></a>&nbsp;kh&ocirc;ng hề kh&oacute; như nhiều bạn vẫn nghĩ. Mọi sự th&agrave;nh c&ocirc;ng đều c&oacute; gi&aacute; của n&oacute;. V&igrave; vậy, việc bạn cần l&agrave;m l&agrave; h&atilde;y thật chăm chỉ v&agrave; r&egrave;n luyện cho m&igrave;nh những kỹ năng, học hỏi, t&igrave;m t&ograve;i những kiến thức mới, lu&ocirc;n cập nhật xu hướng v&agrave; quan trọng l&agrave; bạn phải thật sự c&oacute; đam m&ecirc; với nghề.</p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: center;\"><img style=\"position: relative; max-width: 100%; height: 538px; width: 720px;\" src=\"https://resources.mindx.edu.vn/uploads/images/L%E1%BA%ADp%20tr%C3%ACnh%20game%20c%E1%BA%A7n%20h%E1%BB%8Dc%20nh%E1%BB%AFng%20g%C3%AC%20-%203.png\" alt=\"Lập tr&igrave;nh game cần học những g&igrave; - 2\" /></p>\r\n<h2 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\">2. Lập tr&igrave;nh game cần học những g&igrave; để nhanh ra sản phẩm?</h2>\r\n<h3 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\"><em>2.1 Lu&ocirc;n c&oacute; những &yacute; tưởng hay v&agrave; tuyệt vời</em></h3>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Đ&acirc;y l&agrave; một trong những yếu tố đầu ti&ecirc;n cần phải c&oacute; của một lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n l&agrave;m game. Trước khi bắt tay v&agrave;o thực hiện một game n&agrave;o đ&oacute;, bạn cần phải c&oacute; &yacute; tưởng cho game đ&oacute; thật tốt. Từ nh&acirc;n vật, bối cảnh, m&agrave;u sắc, h&agrave;nh động của c&aacute;c nh&acirc;n vật trong game&hellip; H&atilde;y tập l&ecirc;n &yacute; tưởng trong đầu v&agrave; cần thận ghi ch&eacute;p lại n&oacute; khi bạn cảm thấy n&oacute; l&agrave; một &yacute; tưởng hay v&agrave; tuyệt vời. Đ&oacute; l&agrave; tiền đề để bạn c&oacute; thể triển khai.</p>\r\n<h3 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\"><em>2.2 Am hiểu về game</em></h3>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Điều n&agrave;y thật sự quan trọng đối với c&aacute;c lập tr&igrave;nh vi&ecirc;n game trong tương lai. Nếu bạn kh&ocirc;ng am hiểu về game th&igrave; đồng nghĩa với việc bạn kh&ocirc;ng thể n&agrave;o tạo n&ecirc;n ch&uacute;ng. H&atilde;y sử dụng v&agrave; chơi thật nhiều thể loại game để biết rằng m&igrave;nh y&ecirc;u th&iacute;ch thể loại n&agrave;o v&agrave; th&ocirc;ng qua đ&oacute; t&igrave;m hiểu những nhu cầu thiết yếu của người chơi. Đồng thời, c&oacute; những &yacute; tưởng hay cũng như biết c&aacute;ch khắc phục những thiếu x&oacute;t của ứng dụng game trong tương lai của bạn.</p>\r\n<blockquote style=\"border-left-color: #5e35b1; margin-left: 0px; padding-left: 5px; color: #5e35b1; font-family: Roboto, Lato, sans-serif; font-size: 20px;\">\r\n<p style=\"margin-left: 40px;\"><u><span style=\"font-weight: bold;\">&gt;&gt;&gt;&gt;&nbsp;<a style=\"opacity: 1; text-decoration-line: none; transition: all 0.2s ease-in-out 0s;\" href=\"https://mindx.edu.vn/blog/post/lap-trinh-game-nhung-dieu-can-biet\">Đ&atilde; học lập tr&igrave;nh game, phải biết tất tần tật những điều n&agrave;y!&nbsp;</a></span></u></p>\r\n</blockquote>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: center;\"><img style=\"position: relative; max-width: 100%; height: 543px; width: 720px;\" src=\"https://resources.mindx.edu.vn/uploads/images/L%E1%BA%ADp%20tr%C3%ACnh%20game%20c%E1%BA%A7n%20h%E1%BB%8Dc%20nh%E1%BB%AFng%20g%C3%AC%20-%204.png\" alt=\"Lập tr&igrave;nh game cần học những g&igrave; - 3\" /></p>\r\n<h3 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\"><em>2.3 C&oacute; một ch&uacute;t kiến thức về To&aacute;n học, Vật l&yacute;</em></h3>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Với những thể loại game chuyển động, kiến thức về những bộ m&ocirc;n n&agrave;y ngo&agrave;i việc gi&uacute;p tư duy lập tr&igrave;nh của bạn được tốt hơn, n&oacute; sẽ c&ograve;n gi&uacute;p bạn c&oacute; thể m&ocirc; tả chuyển động của nh&acirc;n vật chuẩn x&aacute;c v&agrave; &ldquo;mượt m&agrave;&rdquo; hơn.</p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">B&ecirc;n cạnh đ&oacute;, mỗi thể loại game sẽ cần &aacute;p dụng c&aacute;c kiến thức về To&aacute;n học v&agrave; Vật l&yacute; kh&aacute;c nhau. Ch&iacute;nh v&igrave; thế, ngo&agrave;i việc học hỏi từ những kiến thức phổ th&ocirc;ng trước đ&acirc;y bạn c&oacute; thể tham khảo th&ecirc;m tr&ecirc;n Google.</p>\r\n<h3 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\"><em>2.4 Kiến thức về thiết kế, đồ họa</em></h3>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Thực tế cho thấy, việc thiết kế, đồ họa đẹp sẽ khiến trờ chơi của bạn trở n&ecirc;n hấp dẫn hơn rất nhiều nhất l&agrave; đối với những game c&oacute; bối c&aacute;nh phức tạp v&agrave; c&aacute;c nh&acirc;n vật chuyển động nhiều. Ngo&agrave;i ra, n&oacute; c&ograve;n ảnh hưởng đến cả qu&aacute; tr&igrave;nh viết code, xử l&yacute; c&aacute;c diễn biến trong game. Ch&iacute;nh v&igrave; thế, kiến thức về lập tr&igrave;nh quan trọng bao nhi&ecirc;u, th&igrave; đồ họa cũng vậy. V&igrave; thế, bạn nhất định phải c&oacute; những kiến thức v&agrave; kỹ năng về việc thiết kế đồ họa.</p>\r\n<h3 style=\"font-family: Roboto, Lato, sans-serif; margin-left: 40px;\"><em>2.5&nbsp;Th&agrave;nh thạo &iacute;t nhất một ng&ocirc;n ngữ lập tr&igrave;nh</em></h3>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px;\">T&ugrave;y thuộc v&agrave;o nhu cầu cũng như thiết bị của bạn m&agrave; từ đ&oacute; bạn c&oacute; thể lựa chọn cho m&igrave;nh một trong những ng&ocirc;n ngữ lập tr&igrave;nh phổ biến dưới đ&acirc;y:</p>\r\n<ul style=\"list-style-position: inside; font-family: Roboto, Lato, sans-serif; font-size: 20px;\">\r\n<li>\r\n<p style=\"margin-left: 40px;\"><em>Java: thường d&ugrave;ng để viết game cho Mobile - c&aacute;c d&ograve;ng phone hổ trợ J2ME, hoặc viết game cho Android.</em></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-left: 40px;\"><em>C#: D&ugrave;ng viết game cho Windows mobiles, Windows Phone hoặc PC.</em></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-left: 40px;\"><em>Javascript: d&ugrave;ng cho m&ocirc;i trường Web.</em></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-left: 40px;\"><em>Objective-C: D&ugrave;ng cho iOS như m&aacute;y MAC, iPhone 6, iPad.</em></p>\r\n</li>\r\n<li>\r\n<p style=\"margin-left: 40px; text-align: justify;\"><em>C/C++: với sự l&acirc;u đời cũng như được sự hưởng ứng rộng r&atilde;i từ hầu hết c&aacute;c chương tr&igrave;nh đ&agrave;o tạo đại học, C/C++ được xem l&agrave; ng&ocirc;n ngữ cơ bản của mọi ng&ocirc;n ngữ lập tr&igrave;nh, v&agrave; c&oacute; lẽ &iacute;t nhất một lần trong đời th&igrave; mỗi lập tr&igrave;nh vi&ecirc;n đều từng đụng đến n&oacute;. Do đ&oacute;, C/C++ cũng l&agrave; một ng&ocirc;n ngữ kh&aacute; được ưa chuộng trong lập tr&igrave;nh game ng&agrave;y nay, với khả năng thực thi tr&ecirc;n nhiều nền tảng.</em></p>\r\n</li>\r\n</ul>\r\n<blockquote style=\"border-left-color: #5e35b1; margin-left: 0px; padding-left: 5px; color: #5e35b1; font-family: Roboto, Lato, sans-serif; font-size: 20px;\">\r\n<p style=\"margin-left: 40px;\">&gt;&gt;&gt;&nbsp;<a style=\"opacity: 1; text-decoration-line: none; transition: all 0.2s ease-in-out 0s;\" href=\"https://mindx.edu.vn/blog/post/ngon-ngu-lap-trinh-nao-de-hoc-nhat\"><u><span style=\"font-weight: bold;\">Mới bắt đầu th&igrave; n&ecirc;n học ng&ocirc;n ngữ lập tr&igrave;nh n&agrave;o?&nbsp;</span></u></a></p>\r\n</blockquote>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: center;\"><img style=\"position: relative; max-width: 100%; height: 463px; width: 720px;\" src=\"https://resources.mindx.edu.vn/uploads/images/L%E1%BA%ADp%20tr%C3%ACnh%20game%20c%E1%BA%A7n%20h%E1%BB%8Dc%20nh%E1%BB%AFng%20g%C3%AC%20-%205.png\" alt=\"Lập tr&igrave;nh game cần học những g&igrave; - 4\" /></p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Ngo&agrave;i ra để c&oacute; những kiến thức cũng như định hướng b&agrave;i bản nhất, chuy&ecirc;n nghiệp nhất về nghề lập tr&igrave;nh game cũng như việc học lập tr&igrave;nh game cần học những g&igrave; th&igrave; bạn c&oacute; thể tự học tr&ecirc;n c&aacute;c trang web dạy lập tr&igrave;nh game miễn ph&iacute; ở tr&ecirc;n Internet . Hoặc t&igrave;m đến c&aacute;c&nbsp;<a style=\"opacity: 1; text-decoration-line: none; transition: all 0.2s ease-in-out 0s;\" href=\"https://mindx.edu.vn/blog/post/hoc-lap-trinh-game-o-dau\"><u><span style=\"font-weight: bold;\">trung t&acirc;m dạy lập tr&igrave;nh game uy t&iacute;n v&agrave; chuy&ecirc;n nghiệp</span></u></a>&nbsp;như MindX.</p>\r\n<p style=\"font-family: Roboto, Lato, sans-serif; font-size: 20px; margin-left: 40px; text-align: justify;\">Với đội ngũ l&agrave; c&aacute;c lập tr&igrave;nh vi&ecirc;n chuy&ecirc;n nghiệp, c&aacute;c chuy&ecirc;n gia đầu ng&agrave;nh, MindX tự h&agrave;o l&agrave; ng&ocirc;i nh&agrave; chắp c&aacute;nh cho ước mơ của c&aacute;c lập tr&igrave;nh vi&ecirc;n game trong tương lai. H&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i ngay để c&oacute; thể lựa chọn cho m&igrave;nh một kh&oacute;a học lập tr&igrave;nh game ph&ugrave; hợp với khả năng v&agrave; nhu cầu nhất của bạn!</p>');

